import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Usuario } from 'src/app/models/usuario.model';

@Component({
  selector: 'app-cadastrar-usuario',
  templateUrl: './cadastrar-usuario.component.html',
  styleUrls: ['./cadastrar-usuario.component.css']
})
export class CadastrarUsuarioComponent {

  ultimoUsuarioCPF: number = 0;

  nome: string = "";
  idade: string = "";

  usuarios: Usuario[] = [];

  constructor(private client: HttpClient,
    private router: Router){
    }

    ngOnInit(): void{
      this.client.get<Usuario[]>
        ("https://localhost:7035/api/genero/listar")
        .subscribe({
          next: (usuarios) =>{
            this.usuarios = usuarios;
          }, 
          error: (erro) =>{
            console.log(erro);
          }
        })
    }

    cadastrar(): void{

      if(this.usuarios.length > 0){
        const ultimoUsuario: Usuario = this.usuarios[this.usuarios.length - 1];
        if(ultimoUsuario.cpf !== undefined){
          this.ultimoUsuarioCPF = ultimoUsuario.cpf;
          console.log('Ultimo generoID:', this.ultimoUsuarioCPF);
        } else{
          console.log("O último genero não possui um generoID definido");
        }
      } else{
        console.log('A lista de usuarios está vazia');
      }
  
      this.ultimoUsuarioCPF += 1;
  
      let usuario: Usuario = {
        cpf: this.ultimoUsuarioCPF,
        nome: this.nome,
        idade: Number.parseInt(this.idade)
      }

      this.client.post<Usuario>
        ("https://localhost:7035/api/usuario/cadastrar", usuario)
        .subscribe({
          next: (usuario) =>{
            this.router.navigate(['pages/usuario/listar'])
          },
          error: (erro) =>{
            console.log(erro)
          }
        })
    }

}
